import axios from 'axios';

const getImages = (query) => {

    const imageResults = [];

    if (!query) {
        return Promise.resolve([]);
    } else {
        return axios
          .get(`https://images-api.nasa.gov/search?q=${query}`)
          .then((response) => {
              imageResults(response)
                  })
          .catch((err) => {
            console.log(err);
          });
      }
}

export default getImages;
